import React, { useState, useEffect } from 'react';
import './Compare.css'; 
import carImage from './images/car.JPG'; 
import carFlipImage from './images/carflip.JPG';

function Compare() {
  const [carMakes, setCarMakes] = useState([]);
  const [brand1, setBrand1] = useState('');
  const [brand2, setBrand2] = useState('');
  const [model1, setModel1] = useState('');
  const [model2, setModel2] = useState('');
  const [mileage1, setMileage1] = useState('');
  const [mileage2, setMileage2] = useState('');
  const [comparisonResult, setComparisonResult] = useState('');
  const [dropdown1, setDropdown1] = useState([]);
  const [dropdown2, setDropdown2] = useState([]);
  const [showDropdown1, setShowDropdown1] = useState(false);
  const [showDropdown2, setShowDropdown2] = useState(false);

  useEffect(() => {
    const loadCarMakes = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/cars');
        const data = await response.json();
        setCarMakes([...new Set(data.map(car => car.make))]); // Avoid duplicates
        console.log('Car makes loaded:', data);
      } catch (error) {
        console.error('Error fetching car makes:', error);
      }
    };
    loadCarMakes();
  }, []);

  const filterMakes = (brand, setDropdown, setShowDropdown) => {
    if (brand === '') {
      setDropdown([]); 
      setShowDropdown(false); // Hide dropdown if input is empty
      return;
    }
    const filteredMakes = carMakes.filter(make =>
      make.toLowerCase().includes(brand.toLowerCase())
    );
    setDropdown(filteredMakes);
    setShowDropdown(filteredMakes.length > 0); // Show dropdown if there are matches
  };

  const handleCompare = async () => {
    try {
      const response1 = await fetch(`http://localhost:3000/api/cars?make=${brand1}&model=${model1}`);
      const response2 = await fetch(`http://localhost:3000/api/cars?make=${brand2}&model=${model2}`);
      const data1 = await response1.json();
      const data2 = await response2.json();

      if (data1.length && data2.length) {
        const winner = compareCarData(data1[0], data2[0]);
        displayComparison(winner, data1[0], data2[0]);
      } else {
        setComparisonResult("One or both cars were not found.");
      }
    } catch (error) {
      console.error('Error fetching car data:', error);
      setComparisonResult("Error fetching car data.");
    }
  };

  const compareCarData = (data1, data2) => {
    return data1.rating > data2.rating ? data1 : data2;
  };

  const displayComparison = (winner, data1, data2) => {
    setComparisonResult(`
      <h2>Comparison Result</h2>
      <p>Car 1: ${data1.make} ${data1.model} - Mileage: ${data1.mileage} - Rating: ${data1.rating}</p>
      <p>Car 2: ${data2.make} ${data2.model} - Mileage: ${data2.mileage} - Rating: ${data2.rating}</p>
      <h3>Winner: ${winner.make} ${winner.model}</h3>
    `);
  };

  return (
    <div id="content">
      <h1>Car vs. Car</h1>
      <div className="comparison-container">
        {/* Box 1 */}
        <div className="comparison-box">
          <img src={carImage} alt="Car 1" className="car-image" />
          <div className="input-container">
            <input
              type="text"
              value={brand1}
              onChange={(e) => {
                setBrand1(e.target.value);
                filterMakes(e.target.value, setDropdown1, setShowDropdown1);
              }}
              placeholder="Enter Car 1 Brand"
              onFocus={() => setShowDropdown1(true)}
            />
            {showDropdown1 && dropdown1.length > 0 && (
              <div className="dropdown">
                {dropdown1.map(make => (
                  <div
                    key={make}
                    className="dropdown-item"
                    onClick={() => {
                      setBrand1(make);
                      setDropdown1([]);
                      setShowDropdown1(false); // Hide dropdown on selection
                    }}
                  >
                    {make}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="input-container">
            <input
              type="text"
              value={model1}
              onChange={(e) => setModel1(e.target.value)}
              placeholder="Enter Car 1 Model"
            />
          </div>
          <div className="input-container">
            <input
              type="text"
              value={mileage1}
              onChange={(e) => setMileage1(e.target.value)}
              placeholder="Enter Car 1 Mileage"
            />
          </div>
        </div>

        {/* Box 2 */}
        <div className="comparison-box">
          <img src={carFlipImage} alt="Car 2" className="car-image" />
          <div className="input-container">
            <input
              type="text"
              value={brand2}
              onChange={(e) => {
                setBrand2(e.target.value);
                filterMakes(e.target.value, setDropdown2, setShowDropdown2);
              }}
              placeholder="Enter Car 2 Brand"
              onFocus={() => setShowDropdown2(true)}
            />
            {showDropdown2 && dropdown2.length > 0 && (
              <div className="dropdown">
                {dropdown2.map(make => (
                  <div
                    key={make}
                    className="dropdown-item"
                    onClick={() => {
                      setBrand2(make);
                      setDropdown2([]);
                      setShowDropdown2(false); // Hide dropdown on selection
                    }}
                  >
                    {make}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="input-container">
            <input
              type="text"
              value={model2}
              onChange={(e) => setModel2(e.target.value)}
              placeholder="Enter Car 2 Model"
            />
          </div>
          <div className="input-container">
            <input
              type="text"
              value={mileage2}
              onChange={(e) => setMileage2(e.target.value)}
              placeholder="Enter Car 2 Mileage"
            />
          </div>
        </div>
      </div>
      <div>
        <button onClick={handleCompare} className="center-button">
          Compare
        </button>
      </div>
      <div id="comparisonResult" dangerouslySetInnerHTML={{ __html: comparisonResult }} />
    </div>
  );
}

export default Compare;
